function Button(props){
      return(
      <button className="button" onClick={props.onClick}>
       {props.value}
      </button>
    )
  }
function PasGen(props,code){
    var ln=props,
    sm="abcdefghijklmnopqrstuvwxyz",
    sm_cifr="1234567890",
    sm_spec="!@#$%^&*?/<>",
    sm_tab="ABCDEFGHIJKLMNOPQRSTUVWXYZ",
    word="";
    for (var i = 0, n = sm.length; i < ln; ++i) {
        word += sm.charAt(Math.floor(Math.random() * n));
    }
    return word;
}
class Menu extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
    Buttons: Array(3).fill('off'),
    IsButtonson: Array(3).fill(false),
    };
  }
  GeneratingPassword(lenght){
    var ln=lenght,
    sm="abcdefghijklmnopqrstuvwxyz",
    sm_cifr="1234567890",
    sm_spec="!@#$%^&*?/<>",
    sm_tab="ABCDEFGHIJKLMNOPQRSTUVWXYZ",
    word="";
    if(this.state.IsButtonson[0]==true){
      sm+=sm_cifr;
    }
    if(this.state.IsButtonson[1]==true){
      sm+=sm_spec;
    }
    if(this.state.IsButtonson[2]==true){
      sm+=sm_tab;
    }
    for (var i = 0, n = sm.length; i < ln; ++i) {
        word += sm.charAt(Math.floor(Math.random() * n));
    }
    return <div>{word}</div>;
  }
  handleClick(i) {
        this.setState({isButtonson:!this.state.isButtonson})
        if(this.state.isButtonson==false){
          this.state.Buttons[i] = 'off';
        }
        else{
          this.state.Buttons[i] = 'on';
        }
      }
  renderButton(i){
    return<Button value={this.state.Buttons[i]}
           onClick={() => this.handleClick(i)}
            />;
  }
  renderPass(i){
    var pass="";
    pass=PasGen(i);
    return <div>{pass}</div>;
  }
  render(){
    return(
    <div>
      <div>
      Цифры
      {this.renderButton(0)}
      </div>
      <div>
      Спецсимволы
      {this.renderButton(1)}
      </div>
      <div>
      Табуляция
      {this.renderButton(2)}
      </div>
      <div>
      {this.GeneratingPassword(7)}
      </div>
    </div>
    )
  }
}
ReactDOM.render(
<Menu />,
document.getElementById('qwe')
);
